input_array = input_string.chars

pp input_array

class Building
  attr_accessor :floor
  def initialize
    @floor = 0
  end
  def up_or_down(char)
    if char == '('
      @floor += 1
    else
      @floor -= 1
    end
  end
end

building = Building.new
input_array.each_with_index do |char, index|
  building.up_or_down(char)
  if building.floor == -1
    puts "entered -1 on character: #{index}"
  end
end

puts building.floor